import React, { useState } from "react";
import "./Signup.css";
import { Link, useHistory } from 'react-router-dom';

function Signup() {
  const history = useHistory(); // useHistory hook

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    cPassword: ''
  });

  const [loading, setLoading] = useState(false);
  const [notification, setNotification] = useState(null);

  const { name, email, password, cPassword } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const postData = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/;

    // Checking email
    if (!emailRegex.test(email)) {
      setNotification("Invalid email");
      return;
    } else if (!passRegex.test(password)) {
      setNotification(
        "Password must contain at least 8 characters, including at least 1 number and 1 includes both lower and uppercase letters and special characters for example #,?,!"
      );
      return;
    } else if (password !== cPassword) {
      setNotification("Password and Confirm Password do not match");
      return;
    }

    setLoading(true);
    fetch("/signup", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: name,
        email: email,
        password: password,
        cPassword: cPassword,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.error) {
          setNotification(data.error);
        } else {
          setNotification(data.message);
          history.push("/login"); // Use history.push for navigation
        }
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className="Signup_main">
      <div className="login_container">
        <h1>Welcome To Pocket Saving</h1>
        <div className="signup_form">
          <div className="notification">{notification}</div> {/* Notification Component */}
          <div className="input_form">
            <label htmlFor="name">Name</label>
            <input type="text" id="name" name="name" placeholder="Enter Your Name" value={name} onChange={handleChange} />
          </div>
          <div className="input_form">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" placeholder="Enter Your Email" value={email} onChange={handleChange} />
          </div>
          <div className="input_form">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" name="password" placeholder="Enter Your Password" value={password} onChange={handleChange} />
          </div>
          <div className="input_form">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input type="password" id="confirmPassword" name="cPassword" placeholder="Enter Your Confirm Password" value={cPassword} onChange={handleChange} />
          </div>
          <div className="input_form">
            <button disabled={loading} onClick={postData}>Submit</button>
          </div>
        </div>
        <div className="login_content">
          <p>I have an account <Link to="/login">Login</Link></p>
        </div>
      </div>
    </div>
  );
}

export default Signup;
