import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Home from "./Components/Home";
import Login from "./Components/Login";
import Signup from "./Components/Signup";
import Alllist from "./Components/Alllist";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const userLoggedIn = checkIfUserIsLoggedIn();
    setIsLoggedIn(userLoggedIn);
  }, []);

  // Function to check if the user is logged in based on the presence of a token in localStorage
  function checkIfUserIsLoggedIn() {
    const token = localStorage.getItem("token");
    return !!token;
  }

  // Function to handle successful login and update isLoggedIn state
  function handleLoginSuccess() {
    setIsLoggedIn(true);
  }

  return (
    <BrowserRouter>
      <div className="App">
        {/* Conditionally render the navigation to home page */}
        {isLoggedIn ? <Navigate to="/home" /> : null}

        {/* Define routes */}
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/home" element={<Home />} />
          <Route path="/alllist" element={<Alllist />} />
          <Route path="/" element={<Navigate to="/home" />} />
        </Routes>
      </div>
      <ToastContainer theme="dark" />
    </BrowserRouter>
  );
}

export default App;
