import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./Home.css";

function Home() {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [userEmailInitial, setUserEmailInitial] = useState("");

  // Fetch user's account balance and transactions
  useEffect(() => {
    // Fetch user's account balance
    axios.get("/user/balance")
      .then(response => {
        setBalance(response.data.balance);
      })
      .catch(error => {
        console.error("Error fetching user's account balance:", error);
      });

    // Fetch today's transactions
    axios.get("/transactions/today")
      .then(response => {
        setTransactions(response.data.transactions);
      })
      .catch(error => {
        console.error("Error fetching today's transactions:", error);
      });

    // Fetch user's email and extract the first letter
    axios.get("/user/email")
      .then(response => {
        const userEmail = response.data.email;
        setUserEmailInitial(userEmail.charAt(0).toUpperCase());
      })
      .catch(error => {
        console.error("Error fetching user's email:", error);
      });
  }, []);

  return (
    <div className="Signup_main1">
      <div className="login_container1">
        <div className="navbar">
          <h1>Pocket Saving</h1>
          <div className="user-name">
            <h2>{userEmailInitial}</h2>
          </div>
        </div>

        <div className="account_saving">
          <h2>Account Balance</h2>
          <div className="amount">
            <h2>{balance}</h2>
            <i className="fa-solid fa-pen"></i>
          </div>
        </div>
        <div className="content_text">
          <h4>Spending Money</h4>
          <div className="input_form1">
            <input type="text" placeholder="Products..." />
            <input type="number" placeholder="Amounts..." />
          </div>
          <button>Submit</button>
        </div>

        <div className="transaction_details">
          <p>Today Transactions</p>
          <Link to="/alllist">
            {" "}
            <p>View All</p>
          </Link>
        </div>
        <div className="products_details_list">
          <h4>Products</h4>
          <ul>
            {transactions.map((transaction, index) => (
              <li key={index}>
                <span>{transaction.product}</span>
                <span>- {transaction.amount}</span>
              </li>
            ))}
          </ul>
          <div className="total_ammount">
            <span>Total</span>
            <span>- {transactions.reduce((total, transaction) => total + transaction.amount, 0)}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
