import React, { useState } from "react";
import "./Alllist.css";
import Select from "react-select";
import { Link } from "react-router-dom";
function Alllist() {
  const [selectedOption, setSelectedOption] = useState(null);








  const options = [
    { value: "today", label: "Today All" },
    { value: "All", label: "All" },
    { value: "january", label: "January" },
    { value: "february", label: "February" },
    { value: "march", label: "March" },
    { value: "april", label: "April" },
    { value: "may", label: "May" },
    { value: "june", label: "June" },
    { value: "july", label: "July" },
    { value: "august", label: "August" },
    { value: "september", label: "September" },
    { value: "october", label: "October" },
    { value: "november", label: "November" },
    { value: "december", label: "December" },
  ];

    // Replace 'transactions' with your actual data source
    const transactions = [
      { product: "Groceries", date: "04-May-2024", amount: 100 },
      // Add more transactions as needed
    ];
  
    // Filtered list of transactions based on selected option
    const filteredTransactions = getFilteredTransactions(selectedOption);
  
    // Function to filter transactions based on selected option
    function getFilteredTransactions(selectedOption) {
      // Implement your logic to filter transactions based on the selected option
      // For now, returning the original list as an example
      return transactions;
    }

  return (
    <div className="view_all">
      <div className="title">
      <p>
        <Link className="link" to="/home">
     
          {" "}
          <i class="fa-solid fa-arrow-left"></i>
        </Link></p>
        <h1>View All Transactions</h1>
      </div>

      <div className="btn_viewall">
        <div className="dropdown">
          <Select options={options}
            value={selectedOption}
            onChange={setSelectedOption} />
        </div>
        <div className="export_pdf">
          <button>Export Pdf</button>
        </div>
      </div>
      <div className="main_list">
        <div className="item_list_title">
          <p>Products</p>
          <p>Date and Time</p>
          <p>Amount</p>
        </div>
        <div className="transaction_details_lists">
        <ul>
            {filteredTransactions.map((transaction, index) => (
              <li key={index}>
                <span>{transaction.product}</span>
                <span>{transaction.date}</span>
                <span className="red_span">- {transaction.amount}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Alllist;
