import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Home from "./Components/Home";
import Login from "./Components/Login";
import Signup from "./Components/Signup";
import Alllist from "./Components/Alllist";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const userLoggedIn = checkIfUserIsLoggedIn(); 

    setIsLoggedIn(!userLoggedIn);
  }, []);

  const handleLogout = () => {
    // Clear the token from localStorage or perform any other logout logic
    localStorage.removeItem("token");
    setIsLoggedIn(false);
  };

  return (
    <BrowserRouter> 
      <div className="App">
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login onLogin={() => setIsLoggedIn(true)} />} />
          <Route path="/home" element={isLoggedIn ? <Home onLogout={handleLogout} /> : <Navigate to="/login" />} />
          <Route path="/alllist" element={isLoggedIn ? <Alllist onLogout={handleLogout} /> : <Navigate to="/login" />} />
          <Route path="/" element={<Navigate to="/home" />} />
        </Routes>
      </div>
      <ToastContainer theme="dark" />
    </BrowserRouter>
  );
}

function checkIfUserIsLoggedIn() {
  const token = localStorage.getItem("token");
  return !!token; 
}

export default App;
