import React, { useState, useEffect } from "react";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom"; // Import Routes
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Home from "./Components/Home";
import Login from "./Components/Login";
import Signup from "./Components/Signup";
import Alllist from "./Components/Alllist";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if user is logged in (you may implement this logic based on your authentication system)
    const userLoggedIn = checkIfUserIsLoggedIn(); // Replace with your authentication logic
    setIsLoggedIn(userLoggedIn);
  }, []);

  return (
    <BrowserRouter>
      <Router>
        <div className="App">
          <Routes>
            {/* Redirect to home page if user is logged in */}
            {isLoggedIn && <Navigate to="/home" />}
            {/* Otherwise, show signup and login routes */}
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            {/* Show home page and other routes */}
            <Route path="/home" element={<Home />} />
            <Route path="/alllist" element={<Alllist />} />
          </Routes>
        </div>
      </Router>
      <ToastContainer theme="dark" />
    </BrowserRouter>
  );
}

// Function to check if user is logged in (replace with your authentication logic)
function checkIfUserIsLoggedIn() {
  // Implement your authentication logic here
  // Example: check if there's a token in localStorage or sessionStorage
  const token = localStorage.getItem("token");
  return !!token; // Return true if token exists, otherwise false
}

export default App;
