import React, { useState } from "react";
import "./Signup.css";
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios'; // Import Axios

function Signup() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const history = useHistory();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/signup', formData); // Adjust the URL according to your backend API
      console.log(response.data); // Assuming your backend sends a success message
      // Redirect to login page after successful signup
      history.push('/login');
    } catch (error) {
      console.error('Signup failed:', error);
      // Handle signup error (display error message to user or perform other actions)
    }
  };

  return (
    <div className="Signup_main">
      <div className="login_container">
        <h1>Welcome To Pocket Saving</h1>
        <div className="signup_form">
          <form onSubmit={handleSubmit}>
            <div className="input_form">
              <label htmlFor="name">Name</label>
              <input type="text" id="name" name="name" placeholder="Enter Your Name" value={formData.name} onChange={handleChange} />
            </div>
            <div className="input_form">
              <label htmlFor="email">Email</label>
              <input type="email" id="email" name="email" placeholder="Enter Your Email" value={formData.email} onChange={handleChange} />
            </div>
            <div className="input_form">
              <label htmlFor="password">Password</label>
              <input type="password" id="password" name="password" placeholder="Enter Your Password" value={formData.password} onChange={handleChange} />
            </div>
            <div className="input_form">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input type="password" id="confirmPassword" name="confirmPassword" placeholder="Enter Your Confirm Password" value={formData.confirmPassword} onChange={handleChange} />
            </div>
            <div className="input_form">
              <button type="submit">Submit</button>
            </div>
          </form>
          <div className="login_content">
            <p>I have an account <Link to="/login">Login</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signup;
