import React, { useState, useEffect } from "react";
import "./Alllist.css";
import Select from "react-select";
import { Link } from "react-router-dom";
import axios from "axios"; 

function Alllist() {
  const [selectedOption, setSelectedOption] = useState({ value: "today", label: "Today All" });
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const options = [
    { value: "today", label: "Today All" },
    { value: "All", label: "All" },
    { value: "january", label: "January" },
    { value: "february", label: "February" },
    // Add more options as needed
  ];

  useEffect(() => {
    fetchFilteredProducts(selectedOption.value);
  }, [selectedOption]);

  const fetchFilteredProducts = async (filterCriteria) => {
    try {
      setLoading(true);
      setError(null);
      const response = await axios.post("http://localhost:5000/filterProducts", {
        email: localStorage.getItem("rememberedEmail"),
        filterCriteria: filterCriteria,
      });
      setFilteredProducts(response.data.products);
      setTotalAmount(response.data.totalAmount);
    } catch (error) {
      setError("Error fetching data");
      console.error("Error fetching filtered products:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="view_all">
      <div className="title">
        <p>
          <Link className="link" to="/home">
            <i className="fa-solid fa-arrow-left"></i>
          </Link>
        </p>
        <h1>View All Transactions</h1>
      </div>

      <div className="btn_viewall">
        <div className="dropdown">
          <Select options={options} value={selectedOption} onChange={setSelectedOption} />
        </div>
        <div className="export_pdf">
          <button>Export Pdf</button>
        </div>
      </div>
      <div className="main_list">
        <div className="item_list_title">
          <p>Products</p>
          <p>Date and Time</p>
          <p>Amount</p>
        </div>
        <div className="transaction_details_lists">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {!loading && filteredProducts.length === 0 && <p>No transactions found</p>}
          {!loading && filteredProducts.length > 0 && (
            <ul>
              {filteredProducts.map((product, index) => (
                <li key={index}>
                  <span>{product.productName}</span>
                  <span>{product.timestamp}</span>
                  <span className="red_span">- {product.productAmount}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Alllist;
